mapper.select:
SELECT count(*)
FROM t_admin
WHERE card = #{card}
AND password = #{password}

SELECT t.card, t.password, t.phone, t.name, t.addr, t.count
FROM t_admin t
WHERE card = #{card}

SELECT t.card, t.password, t.phone, t.name, t.addr, t.count
FROM t_admin t
WHERE addr = #{addr}

mapper.update:
UPDATE t_admin t
SET t.password = #{password}
	, t.count = #{count}
	, t.phone = #{phone}
	, t.name = #{name}
	, t.addr = #{addr}
WHERE t.card = #{card}

UPDATE t_admin
SET password = #{newPwd}
WHERE card = #{card}
AND password = #{oldPwd}

UPDATE t_admin
SET name = #{name}
	, phone = #{phone}
WHERE card = #{card}
