mapper.insert:
		INSERT INTO t_pack (
    		id, org, per_name
			, per_tel, per_addr, addr
			, code, cont_name, cont_tel
			, status, start, end
		) VALUES (
    		#{id}, #{org}, #{per_name}
			, #{per_tel}, #{per_addr}, #{addr}
			, #{code}, #{cont_name}, #{cont_tel}
			, #{status}, #{start}, #{end}
		)

mapper.update:
UPDATE t_pack
SET org = #{org}, per_name = #{per_name}, per_tel = #{per_tel}, per_addr = #{per_addr}
	, addr = #{addr}, code = #{code}, cont_name = #{cont_name}, cont_tel = #{cont_tel}
	, status = #{status}, start = #{start}, end = #{end}
WHERE id = #{id}
