mapper.select:
SELECT count(*)
FROM t_user
WHERE card = #{card}

SELECT count(*)
FROM t_user
WHERE card = #{card}
AND password = #{password}

SELECT count(*)
FROM t_user
WHERE card = #{card}
AND phone = #{phone}

SELECT t.card, t.password, t.phone, t.name, t.addr, t.count
FROM t_user t
WHERE card = #{card}

SELECT t.card, t.password, t.phone, t.name, t.addr, t.count
FROM t_user t
WHERE phone = #{phone}

mapper.insert:
INSERT INTO t_user (
	card, password, phone, name, addr, count
) VALUES (
	#{card}, #{password}, #{phone}, #{name}, #{addr}, #{count}
)

mapper.update:
UPDATE t_user t
SET t.password = #{password}
	, t.count = #{count}
	, t.phone = #{phone}
	, t.name = #{name}
	, t.addr = #{addr}
WHERE t.card = #{card}

UPDATE t_user
SET password = #{password}
WHERE card = #{card}
AND phone = #{phone}

UPDATE t_user
SET password = #{newPwd}
WHERE card = #{card}
AND password = #{oldPwd}

UPDATE t_user
SET name = #{name}
	, addr = #{addr}
WHERE card = #{card}
